import "./contact.scss"
import emailjs from "emailjs-com";
import React from 'react';


function Contact(props) {
    const [result, showResult] = useState(false);
const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('gmail', 'template_60f9ioq', e.target, 'user_6lvljyPRltrXndg1r5pcL')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        }
    );
    e.target.reset();
    showResult
};

    return (
        <div className='contact' id="contact">
            <div className="left">
                <img src="assets/mail4.png" height="300px" className="contact"/>
            </div>
            <div className="right">
                <h1>Contact me.</h1>
                <p>Don´t hesitate to contact me! I’d love to hear about what you’re working on and find a way to work together.</p>
                <br />

                <form action="" onSubmit={sendEmail}>
                    <input className="input100" type="text" name="name" placeholder="Name" required/>
                    <br />
                    <input className="input100" type="email" name="user_email" placeholder="Email" required />
                    <br />
                    <textarea className="input100" name="message" rows="4" placeholder="Message" required/>
                    <br />
                    <input type="submit" value="Send" />
                </form>
            </div>
        </div>

    )
}

export default Contact;
